import React,{Component} from 'react';
import * as ReactBootStrap from "react-bootstrap";
import './App.css';
import TableComp from './TableComp'
import FormComp from './FormComp';
import RadioComp from './RadioComp'
import { Header } from 'semantic-ui-react'

import { ThemeConsumer } from '../node_modules/react-bootstrap/esm/ThemeProvider';
import Scenario1 from './components/scenario1'
import Scenario2 from './scenario2'
import Scenario3 from './scenario3'
import axios from 'axios';
import './form.css'
import logo from './logo.png'
import EpicMenu from './EpicMenu.js'
import { bottom } from '../node_modules/@popperjs/core';
export default class App extends Component {

  constructor(props){
    super(props);
    this.state = {
      visible:false,
      visible_1:false,
      visible_2:false,
      visible_3:false,
      scenario1_data:'',
      scenario2_data:'',
      scenario3_data:'',
      database:'',
    }  
  }

  componentDidMount(){

      fetch('http://localhost:8000/1')
        .then(response=>response.json())
        .then(response=>{
          this.setState({scenario1_data:response})
          console.log("response", response);
        })

      fetch('http://localhost:8000/2')
        .then(response=>response.json())
        .then(response=>{
          this.setState({scenario2_data:response})
          console.log("response", response);
        })

      fetch('http://localhost:8000/3')
        .then(response=>response.json())
        .then(response=>{
          this.setState({scenario3_data:response})
          console.log("response", response);
        })

      fetch('http://localhost:8000/getall')
        .then(response=>response.json())
        .then(response=>{
          this.setState({database:response})
          console.log("response", response);
        })


  }

  viewForm = (e) => {
    this.setState({
      visible:true,
      visible_1:false,
      visible_2:false,
      visible_3:false,

    })
  }

  handleButton1=(e)=>{
    this.setState({
      visible:false,
      visible_1:true,
      visible_2:false,
      visible_3:false,
    })

  }


  handleButton2=(e)=>{
    this.setState({
      visible:false,
      visible_1:false,
      visible_2:true,
      visible_3:false,


    })
  }

  handleButton3=(e)=>{
    this.setState({
      visible:false,
      visible_1:false,
      visible_2:false,
      visible_3:true,

    })
  }
  render()
  {
    console.log(this.state.scenario1_data)

  return (
    <div className="App">

    <ReactBootStrap.Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" 
    style={{padding:"18px"}}
    >
  <ReactBootStrap.Navbar.Brand href="#home">Trade Surveillance System</ReactBootStrap.Navbar.Brand>
  <ReactBootStrap.Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <ReactBootStrap.Navbar.Collapse id="responsive-navbar-nav">
  <ReactBootStrap.Nav className="mr-auto">
  </ReactBootStrap.Nav>

  <ReactBootStrap.Nav className="mr-auto">
  </ReactBootStrap.Nav>


  <ReactBootStrap.Nav className="mr-auto">
  </ReactBootStrap.Nav>

  <ReactBootStrap.Nav className="mr-auto">
