      scenario1_data:[],
      scenario2_data:[],
      scenario3_data:[],
      database:[],
    }  
  }

  async componentDidMount(){

      const url="http://localhost:8000/1";
      const response=await fetch(url);
      const data= await response.json();
      this.setState({
        scenario1_data: data  
      },()=> {
         console.log(this.state.scenario1_data[0][0]); 
