      const data_2= await response_2.json();
      this.setState({
        scenario2_data: data_2  
      },()=> {
         console.log(this.state.scenario2_data[0][0]); 
      });

      const url_3="http://localhost:8000/3";
      const response_3=await fetch(url_3);
      const data_3= await response_3.json();
      this.setState({
        scenario3_data: data_3  
      },()=> {

      });

      const url_table="http://localhost:8000/getall";
      const response_table=await fetch(url_table);
      const data_main= await response_table.json();
      this.setState({
        database: data_main  
      },()=> {
        console.log(this.state.database[0]); 
      });


  }

  viewForm = (e) => {
    this.setState({
      visible:true,
      visible_1:false,
      visible_2:false,
      visible_3:false,

    })
  }

  handleButton1=(e)=>{
    this.setState({
      visible:false,
      visible_1:true,
      visible_2:false,
      visible_3:false,
    })

  }


  handleButton2=(e)=>{
    this.setState({
      visible:false,
      visible_1:false,
      visible_2:true,
      visible_3:false,


    })
  }

  handleButton3=(e)=>{
    this.setState({
      visible:false,
      visible_1:false,
      visible_2:false,
      visible_3:true,

    })
  }
  render()
  {

  return (
    <div className="App">

    <ReactBootStrap.Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" 
    style={{padding:"18px"}}
    >
  <ReactBootStrap.Navbar.Brand href="#home">Trade Surveillance System</ReactBootStrap.Navbar.Brand>
  <ReactBootStrap.Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <ReactBootStrap.Navbar.Collapse id="responsive-navbar-nav">
  <ReactBootStrap.Nav className="mr-auto">
  </ReactBootStrap.Nav>

